mapper.select:
select * from (
  select rownum rnum, rId, bId, rName, rContent, rDate from (
    select rId, bId, rName, rContent, rDate from board_reply
    where bId = #{bId}
    order by rId desc
  )
)
where rnum between #{startCnt} and #{endCnt}

mapper.update:
update board_reply
set rName    = #{rName},
    rContent = #{rContent}
where rId = #{rId}
